// Initialize or retrieve counters and the current state from context storage
let sittingCount = context.get('sittingCount') || 0;
let idleCount = context.get('idleCount') || 0;
let currentState = context.get('currentState') || 'idle';

// Extract distance and motion from the incoming payload
const distance = parseFloat(msg.payload.distance);
const motion = msg.payload.motion === true;

// Determine if the conditions for sitting or idle are met
if (motion && distance < 100) {
    sittingCount++;
    idleCount = 0;  // Reset idle counter since we have a sitting indication
} else if (motion && distance > 100) {
    idleCount++;
    sittingCount = 0;  // Reset sitting counter since we have an idle indication
} else {
    // Reset both counters if neither condition is clearly met
    sittingCount = 0;
    idleCount = 0;
}

// Update the state based on the counters
if (sittingCount >= 2 && currentState !== 'sitting') {
    currentState = 'sitting';
    sittingCount = 0;  // Reset the counter after confirming the state change
} else if (idleCount >= 2 && currentState !== 'idle') {
    currentState = 'idle';
    idleCount = 0;  // Reset the counter after confirming the state change
}

// Store the updated counts and state in the context
context.set('sittingCount', sittingCount);
context.set('idleCount', idleCount);
context.set('currentState', currentState);

// Set the output message payload with state embedded
msg.payload.state = {
    state: currentState,
};

// Return the modified message object to pass it to the next node in the flow
return msg;

// Context variables for timer and notifications
let timer = context.get('timer') || null;
let startTime = context.get('startTime') || null;
let idleStartTime = context.get('idleStartTime') || null;
let elapsedTime = context.get('elapsedTime') || 0;
let lastNotificationTime = context.get('lastNotificationTime') || 0;
let notificationsSent = context.get('notificationsSent') || 0;
let isIdle = context.get('isIdle') || false;

// Incoming data
let currentState = msg.payload.state.state;

// Current timestamp
let now = Date.now();

// Helper function to format time in HH:MM:SS
function formatTime(seconds) {
    const hrs = String(Math.floor(seconds / 3600)).padStart(2, '0');
    const mins = String(Math.floor((seconds % 3600) / 60)).padStart(2, '0');
    const secs = String(seconds % 60).padStart(2, '0');
    return `${hrs}:${mins}:${secs}`;
}

// Notification lists
const idleMessage = "Hi, I'm FitClock, your health assistant!";
const sittingNotifications = [
    "Rise up and move around!",
    "Time for some stretches!",
    "Time to move your body!"
];
const breakBenefits = [
    "Taking breaks from sitting can boost performance",
    "Taking breaks helps to recharge throughout the day",
    "Not sitting for extended periods prevents future health issues",
    "Walking around is the most efficient way to pause sitting"
];
const extendedSittingNotifications = [
    "Time for a short walk!",
    "Go have a snack!",
    "Time for some exercise!",
    "Go outside for some fresh air!",
    "You have been sitting for too long, time to move!"
];

// Function to select a random notification from a list
function getRandomNotification(list) {
    return list[Math.floor(Math.random() * list.length)];
}

// State-based logic
if (currentState === "sitting") {
    if (!timer) {
        // Start the timer if not already running
        startTime = Date.now() - elapsedTime * 1000; // Adjust for accumulated time
        timer = setInterval(() => {
            elapsedTime = Math.floor((Date.now() - startTime) / 1000);
            node.send({ payload: { timer: formatTime(elapsedTime) } });
        }, 1000);
        context.set('startTime', startTime);
        context.set('timer', timer);
        context.set('idleStartTime', null);
    }

    isIdle = false;

    // Send notifications every 30 minutes
    if (now - lastNotificationTime >= 0.5 * 60 * 1000) {
        notificationsSent++;
        if (notificationsSent === 1) {
            msg.notification = getRandomNotification(sittingNotifications);
        } else {
            msg.notification = getRandomNotification(extendedSittingNotifications);
        }
        msg.buzzer = true;
        lastNotificationTime = now;

        // Schedule notification clearance after 3 seconds
        setTimeout(() => {
            node.send({ payload: { notification: "", buzzer: false } });
        }, 3000);
    } else {
        msg.buzzer = false;
    }
} else if (currentState === "idle") {
    if (timer) {
        // Stop the timer and save elapsed time
        clearInterval(timer);
        timer = null;
        elapsedTime = Math.floor((Date.now() - startTime) / 1000);
        node.send({ payload: { timer: formatTime(elapsedTime) } });
        context.set('elapsedTime', elapsedTime);
        context.set('timer', null);
        context.set('startTime', null);
    }

    // Start idle timer if not already started
    if (!idleStartTime) {
        idleStartTime = Date.now();
        context.set('idleStartTime', idleStartTime);
    }

    // Send break benefit notification when the user gets up
    if (!isIdle) {
        msg.notification = getRandomNotification(breakBenefits);
        isIdle = true;
        notificationsSent = 0;
        lastNotificationTime = now;
        msg.buzzer = false;

        // Schedule notification clearance after 3 seconds
        setTimeout(() => {
            node.send({ payload: { notification: "", buzzer: false } });
        }, 3000);
    }

    // Check if idle state has persisted for 5 minutes
    const idleDuration = Math.floor((Date.now() - idleStartTime) / 1000);
    if (idleDuration >= 300) {
        elapsedTime = 0;
        context.set('elapsedTime', 0);
        context.set('idleStartTime', null);
        node.send({ payload: { timer: "00:00:00" } });
    }
} else {
    // Unexpected state
    node.warn(`Unexpected state: ${currentState}`);
    msg.notification = idleMessage;
    msg.buzzer = false;
    if (timer) clearInterval(timer);
    context.set('timer', null);
    context.set('startTime', null);
    context.set('idleStartTime', null);
    context.set('elapsedTime', 0);
    node.send({ payload: { timer: "00:00:00" } });
}

// Update context variables
context.set('timer', timer);
context.set('startTime', startTime);
context.set('elapsedTime', elapsedTime);
context.set('idleStartTime', idleStartTime);
context.set('lastNotificationTime', lastNotificationTime);
context.set('notificationsSent', notificationsSent);
context.set('isIdle', isIdle);

// Return the combined payload
msg.payload = {
    notification: msg.notification || idleMessage,
    buzzer: msg.buzzer || false
};
return msg;

