// Function to update the time in Helsinki
function updateTime() {
    const optionsTime = {
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: true
    };

    const optionsDate = {
        weekday: 'long',
        year: 'numeric',
        month: 'short',
        day: 'numeric'
    };

    // Get Helsinki time and date
    const helsinkiTime = new Date().toLocaleString('en-US', {
        ...optionsTime,
        timeZone: 'Europe/Helsinki'
    });

    const helsinkiDate = new Date().toLocaleString('en-US', {
        ...optionsDate,
        timeZone: 'Europe/Helsinki'
    });

    // Update the clock container
    document.getElementById('time').textContent = helsinkiTime;  // Update time
    document.getElementById('description').textContent = helsinkiDate;  // Update description
}

// Update time every second
setInterval(updateTime, 1000);

// Initial call to display the time immediately
updateTime();




// Connect to the Node-RED WebSocket
const ws = new WebSocket("ws://" + window.location.host + "/fitclock");

// Listen for messages from Node-RED
ws.onmessage = function(event) {
    const data = JSON.parse(event.data); // Parse the incoming JSON

    // Update the timer
    if (data.timer) {
        const sensorDataElement = document.querySelector(".sensor-data");
        if (sensorDataElement) {
            sensorDataElement.textContent = data.timer;
        }
    }

    // Update the notification
    if (data.notification) {
        const notificationElement = document.getElementById("notification-text");
        if (notificationElement) {
            notificationElement.textContent = data.notification;

            // Clear the notification after 3 seconds
            setTimeout(() => {
                notificationElement.textContent = ""; // Clear notification
            }, 3000);
        }
    }
};

// Handle WebSocket errors
ws.onerror = function(error) {
    console.error("WebSocket error:", error);
};

// Log WebSocket connection status
ws.onopen = function() {
    console.log("WebSocket connection established.");
};

ws.onclose = function() {
    console.log("WebSocket connection closed.");
};

body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    margin: 0;
    background-color: #222;
    color: #fff;
}

/* Grid container */
.dashboard {
    display: grid;
    grid-template-columns: 1fr 1fr; /* 2 columns */
    gap: 20px;
    width: 100vw;
    height: 100vh;
    background-color: darkblue;
    padding: 40px;
    border-radius: 10px;
}

/* Container styling */
.container {
    background-color: #333;
    border-radius: 10px;
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
}
/* Flex layout for weather and sensors */
#weather, #sensors {
    flex-direction: row;  /* Align items horizontally */
    justify-content: center; /* Center items horizontally */
    align-items: center;  /* Center items vertically */
}

.weather-icon, .meditation-icon {
    margin-left: 10px;  /* Adds space between the text and icon */
    font-size: 5rem;  /* Adjust the size of the icon */
}

/* Individual container styles */
/*#main_dashboard{
    background-color: darkblue;
}
*/
#weather {
    background-color: #4A90E2;
}

#clock {
    background-color: #34495E;
    justify-content: center;
}

#sensors {
    background-color: #27AE60;
}

#notifications {
    background-color: #E74C3C;
    justify-content: center;
}

/* Sample text styling */
.temp, .time, .sensor-data {
    font-size: 6em;
    font-weight: bold;
}
.notification {
    font-size: 3em;
    font-weight: bold;
}

.description {
    font-size: 2em;
    margin-top: 10px;
    opacity: 0.8;
}
